import React, { useEffect, useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import { useParams } from "react-router-dom";
import { fetchProduct } from "../actions/productActions";
import { Container, Row, Col, Image, Spinner, Button } from "react-bootstrap";
import { addToCart } from "../actions/cartActions";

const SingleProduct = () => {
  const dispatch = useDispatch();

  const { productId } = useParams();
  const [quantity, setQuantity] = useState(0);

  useEffect(() => {
    dispatch(fetchProduct(productId));
  }, []);

  const productFetched = useSelector(state => state.productData);
  const { loading, error, product } = productFetched;
  const handleAddToCart = () => {
    dispatch(addToCart(productId, quantity));
  };
  const handleOnChange = e => {
    setQuantity(e.target.value);
  };

  return (
    <Container>
      {loading ? (
        <Spinner animation="border" role="status">
          <span className="sr-only">Loading...</span>
        </Spinner>
      ) : error ? (
        <div>{error}</div>
      ) : product ? (
        <>
          <Row>
            <Col md={5} lg={3}>
              <Image src={`${process.env.PUBLIC_URL}/${product.image}`} />
            </Col>
            <Col md={7} lg={9}>
              <h2>{product.name}</h2>

              <p>{product.description}</p>
              <br />
              <p>{product.price}</p>
              <br />
              <label>
                quantity
                <input
                  value={quantity}
                  type="text"
                  onChange={handleOnChange}
                  style={{ width: "20px" }}
                ></input>
              </label>
              <br />
              <Button variant="success" onClick={handleAddToCart}>
                Add to Cart
              </Button>
            </Col>
          </Row>
          <Row>
            {product.reviews.map(el => (
              <div></div>
            ))}

            {console.log(product.reviews)}
            <Col md={5} lg={3}>
              <Image src={`${process.env.PUBLIC_URL}/logo192.png`} />
            </Col>
          </Row>
        </>
      ) : (
        <div>product not found</div>
      )}
    </Container>
  );
};

export default SingleProduct;
