import React, { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { useParams } from "react-router-dom";
import { fetchProduct } from "../actions/productActions";
import { Container, Row, Col, Image } from "react-bootstrap";
import { addToCart } from "../actions/cartActions";

const SingleProduct = ({ match }) => {
  const dispatch = useDispatch();

  const { productId } = useParams();
  console.log(match.params.id);
  useEffect(() => {
    dispatch(fetchProduct(productId));
  }, []);

  const handleAddToCart = () => {
    dispatch(addToCart(productId, 2));
  };
  return (
    <Container>
      <Row>
        <Col md={5} lg={3}>
          <Image src={`${process.env.PUBLIC_URL}/logo192.png`} />
        </Col>
        <Col md={7} lg={9}>
          <h2>Product </h2>
          <p>product description</p>
          <p>
            product description product description product description product
            description product description product description product
            descriptionproduct descriptionvproduct descriptionv
          </p>
          <button>Add to Cart</button>
        </Col>
      </Row>

      <Row></Row>
    </Container>
  );
};

export default SingleProduct;
